import { StatusBar } from 'expo-status-bar';
import * as React from 'react';
import { Platform, StyleSheet, TextInput, Button } from 'react-native';
import DisplayPicture from '../components/DisplayPicture';
import CustomButton from '../components/CustomButton';
import { Camera } from 'expo-camera';

import EditScreenInfo from '../components/EditScreenInfo';
import { Text, View } from '../components/Themed';

export default function createNewPost() {
  const [hasCameraPermission, setHasCameraPermission] = React.useState(null);
  const [camera, setCamera] = React.useState(null);
  const [image, setImage] = React.useState(null);
  const [type, setType] = React.useState(Camera.Constants.Type.back);

  React.useEffect(() => {
    (async () => {
      const cameraStatus = await Camera.requestPermissionsAsync();
      setHasCameraPermission(cameraStatus.status === 'granted');


    })();
  }, []);

  const takePicture = async () => {
    if (camera) {
      const data = await camera.takePictureAsync(null);
      setImage(data.uri);
      console.log(data);
    }
  }


  const [postData, setPostData] = React.useState({
    id: '14',
    postType: null,
    img: null,
    text: null,
    desc: null,
    member: null,

  });
  const handleEditChange = text => {
    setPostData({
      id: '14',
      postType: null,
      img: null,
      text: null,
      desc: text,
      member: null,
    });
  };
  console.log(postData.desc);

  // if (hasCameraPermission === null) {
  //   return <View />;
  // }
  // if (hasCameraPermission === false) {
  //   return <Text>No access to camera</Text>;
  // }

  return (
    <View style={styles.container}>
      <View style={styles.textDesc}>
        <View style={styles.user}>
          <DisplayPicture/>
          <Text style={styles.title}>Me</Text>
        </View>

        <TextInput 
        style={styles.textInputArea}
        placeholder="Tap to add text.."
        onChangeText={handleEditChange}
        />

      </View>

      <View style={styles.cameraArea}>
        <Camera
          ref={ref => setCamera(ref)}
          style={styles.fixedRatio}
          type={type}
          ratio={'1:1'} />
      </View>

      <Button
        title="Flip Image"
        onPress={() => {
          setType(
            type === Camera.Constants.Type.back
              ? Camera.Constants.Type.front
              : Camera.Constants.Type.back
          );
        }}>
      </Button>
      {/* <Button title="Take Picture" onPress={() => takePicture()} /> */}

      <View style = {styles.button}>
          <Button title="Take Picture" onPress={() => console.log("I'm pressed")} />
          {/* <CustomButton title='Take photo' textSize={30} textLineHeight={30} /> */}
      </View>

      <View style = {styles.button}>
          <CustomButton title='Post' textSize={30} textLineHeight={30} />
      </View>




    </View>

  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    // flexDirection:'row',
    alignItems: 'center',
    justifyContent: 'center',
    // backgroundColor: 'purple'
  },
  title: {
    fontSize: 20,
    fontWeight: 'bold',
  },
  textDesc:{
    // alignItems: 'flex-start',
    flex: 1,
    justifyContent: 'space-evenly',
    flexDirection:'row',
    width: '100%',
    backgroundColor: 'yellow'

  },
  user:{
    // width: '25%',
    flex: 1,
    backgroundColor: 'red',
    // height: '100%',
    // flexDirection: 'row',
    // alignItems: 'center',
    // justifyContent: 'space-evenly',

},
textInputArea:{
  flex: 5,
  // margin: '5%'
},
cameraArea:{
  flex: 4,
  width: '100%',
  backgroundColor:'blue'
},
button:{
  flex: 1,
  width: '100%'
},
cameraContainer: {
  flex: 1,
  flexDirection: 'row'
},
fixedRatio: {
  flex: 1,
  aspectRatio: 1
}
