import * as React from 'react';
import { StyleSheet, SectionList, FlatList, Button} from 'react-native';

import EditScreenInfo from '../components/EditScreenInfo';
import createNewPost from '../screens/createNewPost'
import TopBar from '../components/TopBar';
import Post from '../components/Post';
import {Member} from '../components/Member';
import {Group} from '../components/Group';

import { Text, View } from '../components/Themed';
import { RootTabScreenProps } from '../types';

const member1 = Member(1, "Lisa Maroon", "Lisa", true, false);
const member2 = Member(2, "Kate Maroon", "Kate", false, false);
const member3 = Member(3, "Lacy Maroon", "Lacy", false, false);
const member4 = Member(4, "Sam Maroon", "Sam", false, false);
const members = [member1, member2, member3, member4];
const myGroup = Group("Maroon Family", members, 4);
const testDescription= 'French Toast for lunch!';
const testText = "YAYYYY!!!!"

export default function TabOneScreen({ navigation, route }: RootTabScreenProps<'TabOne'>) {
  const [postData, setPostData] = React.useState(defaultData);
  const [count, setCount] = React.useState(14);
  React.useEffect(() => {
    navigation.addListener('focus', () => {
      if(route.params){
        setCount(count+1);
        const newPost = {
          id: count.toString(),
          postType: 'imageDesc',
          img: route.params[0],
          text: route.params[1],
          desc: route.params[1],
          member: member1,
        };

        setPostData(
        //   [  {...defaultData[0], data:
        //     [newDataList]}, ...defaultData.slice(1)

        //   ]
        // );
          // ([prevData, ...prevData]) => []
          // defaultData
          [  {...postData[0], data:
            [newPost, ...postData[0].data]}, ...postData.slice(1)

          ]
        );

          console.log(count);
      }


    });
  });

  console.log(postData);
  console.log(route.params[0], route.params[1]);

  return (
    <View style={styles.container}>
      <Button
        onPress={() => navigation.navigate('Modal')}
        title="Create a new post"
      />
      <TopBar group = {myGroup} />
      <View style={styles.bulletinBoard}>
        {/* <Text> {postData[0].data[0].id}</Text> */}

        <View style={styles.postArea}>
            {/* <Post type= 'textOnly' myImg={require('../assets/images/toast.jpg')} text={testText} desc={testDescription} member= {member2} /> */}
          {/* <View style={styles.post}> */}
          {/* <Text style={styles.title}> kajnkajnsdkajndkjsndkajnsdkajndkajsn{postData[0].data[0].id}</Text> */}
          <SectionList
          // horizontal
          // inverted={true}
          contentContainerStyle={{ paddingHorizontal: 10 }}
          stickySectionHeadersEnabled={false}
          sections={postData}
          // extraData={postData}
          renderSectionHeader={({ section }) => (
            <>
              <Text style={styles.title}>{section.title}</Text>
              <FlatList
                horizontal
                data={section.data}
                // extraData={section}
                // inverted={true}
                renderItem={({ item }) => <Post type={item.postType} img={item.img}  text = {item.text} desc = {item.desc} member={item.member}/>}
                keyExtractor={item => item.id}
                showsHorizontalScrollIndicator={false}
              />
            </>
          )}
          renderItem={({ item, section }) => {
            return null;
            // return <ListItem item={item} />;
          }}
        />

        </View>
      </View>

      {/* <View style={styles.separator} lightColor="#eee" darkColor="rgba(255,255,255,0.1)" /> */}
      {/* <EditScreenInfo path="/screens/TabOneScreen.tsx" /> */}
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignContent: 'center',
    justifyContent: 'flex-start',
  },
  title: {
    fontSize: 50,
    fontWeight: 'bold',
    // paddingVertical: '2%',
  },
  bulletinBoard: {
    flex: 1,
    backgroundColor: '#f4f4f4',
    // flexDirection: 'row',
    justifyContent: 'flex-start',
    alignItems: 'center'

  },
  postArea:{
    backgroundColor: '#aaaaaa',
    alignSelf: 'stretch',
    height: '100%',
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'center',
    // paddingBottom: 10

  },
  // post:{
  //   width:'70%',
  //   height: '50%',
  //   backgroundColor: 'blue'
  // }
  // topBar:{

  //   flex: 1
  // }
  // separator: {
  //   marginVertical: 30,
  //   height: 1,
  //   width: '80%',
  // },
});

const defaultData= [
  {
    title: 'Today',
    data: [  {
      id: '10',
      postType: 'imageDesc',
      img: null,
      text: testText,
      desc: testDescription,
      member: member1,

    },
    {
      id: '11',
      postType: 'textOnly',
      img: require('../assets/images/toast.jpg'),
      text: 'null',
      desc: null,
      member: member2,

    },
    {
      id: '12',
      postType: 'textOnly',
      img: require('../assets/images/toast.jpg'),
      text: 'null',
      desc: null,
      member: member3,

    },
    {
      id: '13',
      postType: 'textOnly',
      img: require('../assets/images/toast.jpg'),
      text: 'null',
      desc: null,
      member: member4,

    }


    ]
  },
  {
    title: 'Yesterday',
    data:[  {
      id: '21',
      postType: 'textOnly',
      img: require('../assets/images/toast.jpg'),
      text: 'null',
      desc: testDescription,
      member: member1,
    },
    {
      id: '22',
      postType: 'textOnly',
      img: require('../assets/images/toast.jpg'),
      text: 'null',
      desc: testDescription,
      member: member1,
    }
    ]
  }

];
