import { StatusBar } from 'expo-status-bar';
import * as React from 'react';
import { Platform, StyleSheet, TextInput, Button, Image } from 'react-native';
import DisplayPicture from '../components/DisplayPicture';
import CustomButton from '../components/CustomButton';
import { Camera } from 'expo-camera';
import { RootStackScreenProps } from '../types';

import EditScreenInfo from '../components/EditScreenInfo';
import { Text, View } from '../components/Themed';
import TabOneScreen from './TabOneScreen';

export default function createNewPost({navigation, route }: RootStackScreenProps<'Modal'>) {
  const liveCam = <Camera
                    ref={ref => setCamera(ref)}
                    style={styles.fixedRatio}
                    type={type}
                    ratio={'1:1'} />

  const [hasCameraPermission, setHasCameraPermission] = React.useState(null);
  const [camera, setCamera] = React.useState(null);
  const [image, setImage] = React.useState(null);
  const [type, setType] = React.useState(Camera.Constants.Type.back);
  const [postText, setPostText] = React.useState('');
  const [postButtonTitle, setPostButtonTitle] = React.useState('Back to Bulletin Board');
  const [takePictureButton, setTakePictureButton] = React.useState('Take Picture');
  const [cameraDisplay, setCameraDisplay] = React.useState(liveCam);




  React.useEffect(() => {
    (async () => {
      const cameraStatus = await Camera.requestPermissionsAsync();
      setHasCameraPermission(cameraStatus.status === 'granted');


    })();
  }, []);

  const takePicture = async () => {

    if (camera) {
      const data = await camera.takePictureAsync(null);

      setImage(data.uri);
      setPostButtonTitle('Post');
      setCameraDisplay(<Image source={{uri: data.uri}} style={ {height: '100%', width: '100%'}}/>);
      setTakePictureButton("Remove and Retake Picture");

    }else{
      setImage(null);
      setCameraDisplay(liveCam);
      setTakePictureButton("Take Picture");
    }
  }

  const handleEditChange = (text: string) =>{
    setPostText(text);
    if (!(text === '')){
      setPostButtonTitle('Post');
    }else{
      if (!image){
        setPostButtonTitle('Back to Bulletin Board');
      }

    }
  }


  return (
    <View style={styles.container}>
      <View style={styles.textDesc}>
        <View style={styles.user}>
          <DisplayPicture/>
          <Text style={styles.title}>Me</Text>
        </View>

        <TextInput 
        style={styles.textInputArea}
        placeholder="Tap to add text.."
        onChangeText={handleEditChange}
        />

      </View>

      <View style={styles.cameraArea}>
        {cameraDisplay}
        {/* <Camera
          ref={ref => setCamera(ref)}
          style={styles.fixedRatio}
          type={type}
          ratio={'1:1'} /> */}
      </View>

      <Button
        title="Flip Image"
        onPress={() => {
          setType(
            type === Camera.Constants.Type.back
              ? Camera.Constants.Type.front
              : Camera.Constants.Type.back
          );
        }}>
      </Button>
      {/* <Button title="Take Picture" onPress={() => takePicture()} /> */}
      {/* {image && <Image source={{ uri: image }} style={{ flex: 1 }} />} */}

      <View style = {styles.button}>
          <Button title={takePictureButton} onPress={() => takePicture()} />
          {/* <CustomButton title='Take photo' textSize={30} textLineHeight={30} /> */}
      </View>

      <View style = {styles.button}>
      {/* {image && <Image source={{ uri: image }} style={{ flex: 1 }} />} */}
        <Button title={postButtonTitle} onPress={() => navigation.navigate("Root", {screen: 'TabOne', params: [image, postText]}  )} />
        {/* <CustomButton title='Post' textSize={30} textLineHeight={30} /> */}
      </View>




    </View>

  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    // flexDirection:'row',
    alignItems: 'center',
    justifyContent: 'center',
    // backgroundColor: 'purple'
  },
  title: {
    fontSize: 20,
    fontWeight: 'bold',
  },
  textDesc:{
    // alignItems: 'flex-start',
    flex: 1,
    justifyContent: 'space-evenly',
    flexDirection:'row',
    width: '100%',
    backgroundColor: 'yellow'

  },
  user:{
    // width: '25%',
    flex: 1,
    backgroundColor: 'red',
    // height: '100%',
    // flexDirection: 'row',
    // alignItems: 'center',
    // justifyContent: 'space-evenly',

},
textInputArea:{
  flex: 5,
  // margin: '5%'
},
cameraArea:{
  flex: 4,
  width: '100%',
  // backgroundColor:'blue'
},
button:{
  flex: 1,
  width: '100%'
},
cameraContainer: {
  flex: 1,
  flexDirection: 'row'
},
fixedRatio: {
  flex: 1,
  aspectRatio: 1
}
  // separator: {
  //   marginVertical: 30,
  //   height: 1,
  //   width: '80%',
  // },

});
